%% EG_SAVEDATA
% Demos for saving the different kinds of outputs generated by the YSTAM
% simulator
% See eg_2d_majority or eg_2d_space for an example of ColorCode

n = 40;
ystam(n, ...
        'ShowRun', true, ...
        'ShowECM', true, ...
        'ShowHeatMap', true, ...
        'ExportImages', 'gmh', ...
        'ExportHits', true, ...
        'SaveVideo', 'gmh', ...
        'FrameSkip', 10, ...
        'SavePopulations', true, ... % Only works with ColorCode
        'Filename', 'test1', ...
        'Gse', 0.95, ...
        'BranchingFactor', 0.5, ...
        'ECM', [5 3 0.01 0.1 0.2 0], ...
        'MaxTrials', 1, ...
        'TimeLimit', 100, ...
        'Feedback', true, ...
        'Continuous', true, ...
        'NumSeeds', [1 1], ...
        'SetGoal', [ceil(n/2), n])

n = 200;
ystam(n, ...
        'ShowRun', true, ...
        'ShowECM', true, ...
        'ShowHeatMap', true, ...
        'ExportImages', 'gmh', ...
        'ExportHits', true, ...
        'SaveVideo', 'gmh', ...
        'FrameSkip', 10, ...
        'SavePopulations', true, ... % Only works with ColorCode
        'Filename', 'test2', ...
        'Gse', 0.95, ...
        'BranchingFactor', 0.5, ...
        'ECM', [5 3 0.01 0.1 0.2 0], ...
        'MaxTrials', 1, ...
        'TimeLimit', 100, ...
        'Feedback', true, ...
        'Continuous', true, ...
        'NumSeeds', [1 1], ...
        'SetGoal', [ceil(n/2), n], ...
        'GrowthMode', 'radial', ...
        'SetRoot', [ceil(n/2)  ceil(n/2)])